import React, { useState } from 'react';
import './App.css';
import WalletConnect from './components/WalletConnect';
import AddCertificate from './components/AddCertificate';
import ViewCertificate from './components/ViewCertificate';

function App() {
  const [account, setAccount] = useState('');
  const [provider, setProvider] = useState(null);
  const [activeTab, setActiveTab] = useState('view'); // 'view' ou 'add'

  // Fonction appelée lorsque le portefeuille est connecté
  const handleConnect = (connectedAccount, ethersProvider) => {
    setAccount(connectedAccount);
    setProvider(ethersProvider);
  };

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-indigo-600 via-purple-500 to-pink-500">
      <header className="bg-indigo-600 text-white p-6 flex justify-between items-center shadow-md">
        <h1 className="text-2xl font-bold">Certification de Diplômes</h1>
        <WalletConnect onConnect={handleConnect} />
      </header>

      <main className="flex-1 max-w-7xl mx-auto p-8 w-full">
        <div className="flex mb-8 border-b border-gray-200">
          <button 
            className={`px-6 py-3 font-medium relative ${activeTab === 'view' ? 'text-indigo-600 after:absolute after:bottom-0 after:left-0 after:w-full after:h-1 after:bg-indigo-600' : 'text-gray-700 hover:bg-gray-50'}`}
            onClick={() => setActiveTab('view')}
          >
            Consulter un Diplôme
          </button>
          <button 
            className={`px-6 py-3 font-medium relative ${activeTab === 'add' ? 'text-indigo-600 after:absolute after:bottom-0 after:left-0 after:w-full after:h-1 after:bg-indigo-600' : 'text-gray-700 hover:bg-gray-50'}`}
            onClick={() => setActiveTab('add')}
          >
            Ajouter un Diplôme
          </button>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-8">
          {activeTab === 'view' ? (
            <ViewCertificate provider={provider} />
          ) : (
            <AddCertificate account={account} provider={provider} />
          )}
        </div>
      </main>

      <footer className="bg-indigo-600 text-white text-center p-4 mt-8">
        <p>Certification de Diplômes &copy; {new Date().getFullYear()}</p>
      </footer>
    </div>
  );
}

export default App;
